import React, { Component } from 'react';
import { StyleSheet, Text, View, TextInput, Button } from 'react-native';
import { SearchBar } from 'react-native-elements';


export default class HomeScreen extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            baseURL: 'https://api.edamam.com/search?app_id=c145a587&app_key=d85405c20f6bc0091ccc6d5287f81cc7&q=',
            searchURL: '',
            query: '',
            recipes: []
        }
        this.pressHandler = this.pressHandler.bind(this)
        this.changeHandler = this.changeHandler.bind(this)
        this.submitHandler = this.submitHandler.bind(this)

    }

    pressHandler(navigation) {
        this.props.navigation.navigate('About')
    }
    changeHandler(event) {
        this.setState({ [event.target.id]: event.target.value })

    }
    submitHandler(event) {
        event.preventDefault();
        this.setState({
            searchURL: this.state.baseURL + this.state.query
        }, () => {
            fetch(this.state.searchURL)
                .then(response => { return response.json() })
                .then(json => this.state({
                    recipes: json.recipes,
                    query: ''
                }))
        })
        console.log(this.state.recipes)

    }
    render() {
        return (
            <View style={styles.container}>

                <TextInput onSubmitEditing={this.submitHandler}
                    placeholder="search"
                    onChangeText={text => this.state.query}
                    defaultValue={this.state.query}
                />

            </View>

        )
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#f8f8f8',
        alignItems: 'center'
    },
    search: {
        flex: 0.75,
        flexDirection: 'column',
        justifyContent: 'center',

    }
});
